import React, { useState, useEffect} from "react";
import axios from 'axios';
import '../App.css';

const ProductList = ({ selectedCategory }) => {
const products = [
    {
        id: 1,
        name: "T-Shirt",
        description: "A comfortable cotton t-shirt.",
        price: 19.99,
        category: "clothes",
        imageUrl: "https://www.google.com/search?sca_esv=d621e0682869cae1&sca_upv=1&rlz=1C1UEAD_enUS1112US1112&sxsrf=ADLYWIK5M0OWSqQZxKjNKf8CvufsMjdXlA:1723670231836&q=t-shirt+image&udm=2&fbs=AEQNm0DfIOrc-JVK7JSIXTRypzh1GlMTt1HlvhZFuzqkQ_wIjIPvm2B6oTtYXXz3j23LBQSsHCV2Y3ixMCZ6Yy99zbASosrNFd8YOOrntINuN8oxt8Q-g0jQYBQsKVYPFrE2DXVa1xl5CCby-PRr_Y3cIlndK-naMU0y7AIdZ8--BU4eQcg4zd8pNrWBb-PBumTy-Liot3VmgBr2AdFZPomIqQGFVUGAqg&sa=X&sqi=2&ved=2ahUKEwj07oD_s_WHAxUPRTABHRjBBYEQtKgLegQIEBAB&biw=1280&bih=665&dpr=1.5#vhid=btOZymm3t30o0M&vssid=mosaic"
    },
    {
        id: 2,
        name: "Laptop",
        description: "A high-performance laptop.",
        price: 999.99,
        category: "electronics",
        imageUrl: "https://res.cloudinary.com/your_cloud_name/image/upload/v1627992345/laptop.jpg"
    }
];

const filteredProducts = products.filter(product => product.category === selectedCategory);
    return(
        <div className="product-list">
            {products.map(product => (
                <div key={product.id} className="product-item">
                    <img src ={product.imageUrl} alt={product.name} className="product-image" />
                    <h3>{product.name}</h3>
                    <p>{product.description}</p>
                    <p>${product.price.toFixed(2)}</p>
                </div>
            ))}
        </div>
    );
};

export default ProductList;
